import React from "react";
import styles from "./Hero.module.css";

function Hero() {
  return (
    <div className="hero-section">
      <div className="hero-text-box">
        <p className="heading-primary">
          No worry anymore about choosing your future company
        </p>
        <p className="hero-text">
          It's no secret that companies aren't always completely transparent
          with prospective employees during the recruiting process. Your
          interviewer doesn't always tell you how the boss really treats their
          employees or how equal the opportunities are for employees seeking to
          rise in the ranks. That's where our website comes in handy.
        </p>
      </div>
      <div className="hero-img-box">
        <img
          className="hero-img"
          src={require("../../imgs/home/searching_for_a_job.png")}
          alt="One searching for a job."
        />
      </div>
    </div>
  );
}

export default Hero;
