import React, { useState } from "react";
import styles from "./Nav.module.css";
import Logo from "../Logo/Logo";

function Nav() {
  const [fixNav, setFixNav] = useState(false);

  const fixNavbarHandler = () => {
    if (window.scrollY > 80) {
      setFixNav(true);
      console.log(fixNav);
    } else {
      setFixNav(false);
    }
  };
  window.addEventListener("scroll", fixNavbarHandler);
  return (
    <header className={`${styles.header} ${fixNav ? styles.fixedNav : null}`}>
      <Logo />
      <nav>
        <ul className={styles["main-nav-list"]}>
          <li>
            <a href="#" className={styles["main-nav-link"]}>
              Home
            </a>
          </li>
          <li>
            <a href="#" className={styles["main-nav-link"]}>
              Companies
            </a>
          </li>
          <li>
            <a href="#" className={styles["main-nav-link"]}>
              Salaries
            </a>
          </li>
          <li>
            <a href="#" className={styles["main-nav-link"]}>
              Add my Company
            </a>
          </li>
        </ul>
      </nav>
      <div className={styles["nav-cta-container"]}>
        <a href="#" className={styles["nav-cta-login"]}>
          Log in
        </a>
        <a href="#" className={styles["nav-cta-signup"]}>
          Sign up
        </a>
      </div>
    </header>
  );
}

export default Nav;
