import React, { useState } from "react";
import styles from "./Search.module.css";
import companies from "../../Data.json";

function Search() {
  const [filteredData, setFilteredData] = useState([]);
  const [searchWord, setSearchWord] = useState("");

  const filterHandler = (event) => {
    const searchWord = event.target.value;
    const filteredCompanies = companies.companies.filter((company) => {
      return company.name
        .toLocaleLowerCase()
        .includes(searchWord.toLocaleLowerCase());
    });
    if (searchWord === "") {
      setFilteredData([]);
    } else {
      setFilteredData(filteredCompanies);
    }
  };

  const clearInputHandler = () => {
    setFilteredData([]);
  };

  return (
    <div className={styles.searchSection}>
      <p className="heading-secondary sm-margin">Company searching made easy</p>
      <div className={styles.searchContainer}>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          strokeWidth={1.5}
          stroke="currentColor"
          className={styles.searchIcon}
          onClick={clearInputHandler}
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            d="M21 21l-5.197-5.197m0 0A7.5 7.5 0 105.196 5.196a7.5 7.5 0 0010.607 10.607z"
          />
        </svg>

        <input
          type="text"
          name="company"
          className={styles.input}
          placeholder="Search for a company"
          onChange={filterHandler}
          value={searchWord}
        />
        <div className={styles.closeIconContainer}>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            strokeWidth="1.5"
            stroke="currentColor"
            className={styles.closeIcon}
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M6 18L18 6M6 6l12 12"
            />
          </svg>
        </div>
        <button className="main-btn">Search</button>
      </div>
      {filteredData.length !== 0 && (
        <div className={styles.dataResults}>
          {filteredData.slice(0, 15).map((company) => {
            return (
              <a href="#" target="_blank" className={styles.companyLink}>
                <div>{company.name}</div>
              </a>
            );
          })}
        </div>
      )}
    </div>
  );
}

export default Search;
