import React from "react";
import styles from "./Hero.module.css";

function Hero() {
  return (
    <div className={styles["hero-section"]}>
      <div className={styles["hero-text-box"]}>
        <p className="heading-primary">
          No worry anymore about choosing your future company
        </p>
        <p className={styles["hero-text"]}>
          Companies aren't always completely transparent with prospective
          employees during the recruiting process. Your interviewer doesn't
          always tell you how the boss really treats their employees or how
          equal the opportunities are for employees seeking to rise in the
          ranks. That's where our website comes in handy.
        </p>
      </div>
      <div className={styles["hero-img-box"]}>
        <img
          className={styles["hero-img"]}
          src={require("../../imgs/home/searching_for_a_job.png")}
          alt="One searching for a job."
        />
      </div>
    </div>
  );
}

export default Hero;
